    <script type="text/javascript">
        function report(obj){
            var obj = obj || {};
            var source = {
                pro : "huya_web",//产品给的
                pas : YA.cookie.get('username') || '',
                yyuid : YA.cookie.get('yyuid') || '',
                pageType : "root",
                rso : "",
                rso_desc : "",
                eid : "",
                eid_desc : ""
            }
            for(var property in obj){
               source[property] =  obj[property];
            }
            window.ya = new YA(source.pro,source.pas,{
               pageType: source.pageType,
               yyuid : source.yyuid
            });
            ya.reportProductStartUp({
                pro: source.pro,
                rso: source.rso,
                rso_desc: source.rso_desc
            });
            ya.startProductHeartbeat({
                pro: source.pro,
                rso: source.rso,
                rso_desc: source.rso_desc
            });
            ya.reportProductEvent({
                eid: source.eid,
                eid_desc: source.eid_desc
            })
        }
        var str = location.href;
        var id = str.substring(str.lastIndexOf('/') + 1 );
        report({
            rso: '', //外部来源
            rso_desc:'',//外部来源
            eid: "pageview/blog/product/"+id,  //产品给的
            eid_desc:"pageview/博客/产品/"+id,  //产品给的
            pro:"huya_web"
